var $ = require('jquery');

exports.watch = function() {
  var tabButtons = $('.tabbutton');
  var tabScreens = $('.tabscreen');

  function hideAll() {
    tabScreens.each(function() {
      $(this).css('display', 'none');
    });
  }

  function display(screen) {
    $('.tabscreen[screen="' + screen + '"]').css('display', 'inline-block');
  }

  hideAll();

  tabButtons.each(function() {
    if ($(this).hasClass('selected')) {
      display($(this).attr('screen'));
    }

    $(this).click(function() {
      hideAll();

      display($(this).attr('screen'));

      tabButtons.each(function() {
        $(this).removeClass('selected');
      });

      $(this).addClass('selected');
    });
  });
};
